import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
// import './shop.css'
import ShopHeader from './ShopHeader'
import DealsOfTheDay from './DealsOfTheDay'
import Trending from './Trending'
import ShopByCategory from './ShopByCategory'
import FilterSidebar from './FilterSidebar'
import ProductCard from './ProductCard'
import Footer from '../../../components/Footer'

/**
 * Shop Page Component
 * 
 * E-commerce fashion shop with:
 * - Hero banner with fashion imagery
 * - Trust badges (payment, shipping, returns, quality)
 * - Featured products section
 * - Category navigation
 * - Special offer banner
 * - Product cards with ratings and pricing
 * 
 * 
 */

const MOCK_PRODUCTS = [
  { id: 1, name: 'Classic White Sneakers', price: 129, category: 'SHOES', options: ['6', '7', '8', '9', '+1'], image: 'data:image/jpeg;base64,' },
  { id: 2, name: 'Luxury Leather Handbag', price: 299, category: 'BAGS', options: ['onesize'], image: 'data:image/jpeg;base64,' },
  { id: 3, name: 'Summer Coral Dress', price: 89, category: 'CLOTHING', options: ['XS', 'S', 'M', 'L', '+1'], image: '/path/to/dress.jpg' },
  { id: 4, name: 'Designer Trench Coat', price: 349, category: 'CLOTHING', options: ['XS', 'S', 'M', 'L'], image: '/path/to/trenchcoat.jpg' },
  { id: 5, name: 'Gold Frame Sunglasses', price: 159, category: 'ACCESSORIES', options: ['onesize'], image: '/path/to/sunglasses.jpg' },
  { id: 6, name: 'Pearl Jewelry Set', price: 219, category: 'ACCESSORIES', options: ['onesize'], image: '/path/to/jewelry.jpg' },
];

const Shop = () => {

const [category, setCategory] = useState("All Items");
  const [price, setPrice] = useState(500);
  const [size, setSize] = useState(null);

  const filteredProducts = React.useMemo(() => {
    return MOCK_PRODUCTS.filter((item) => {
      const categoryMatch =
        category === "All Items"
          ? true
          : item.category === category.toUpperCase();

      const priceMatch = item.price <= price;

      const sizeMatch = size ? item.options.includes(size) : true;

      return categoryMatch && priceMatch && sizeMatch;
    });
  }, [category, price, size]);
  

  return (
    <div className="shop-page">
      {/* Navigation Header */}
      <ShopHeader />

      {/* Deals of The Day Section */}
      < DealsOfTheDay />

      {/* Trending Section */}
       <Trending />

      {/* Shop by Category  Products Section */}
       <ShopByCategory />

       {/* product section */}

     <div className="shop-page-container">
      {/* Sidebar */}
      <FilterSidebar
      category={category}
      setCategory={setCategory}
      price={price}
      setPrice={setPrice}
      size={size}
      setSize={setSize}
    />

    <main className="shop-main">

      <h2 className="section-title">
        Products ({filteredProducts.length})
      </h2>

      <div className="shop-grid ">
        {filteredProducts.length ? (
          filteredProducts.map((p) => <ProductCard key={p.id} product={p} />)
        ) : (
          <p className="no-results">No products found.</p>
        )}
      </div>

    </main>
</div>
      <Footer />
    </div>
  )
}

export default Shop
